import React, { Component } from 'react'
import { withRouter } from "react-router-dom";
import {ButtonGroup,Button,PageHeader,Image} from 'react-bootstrap'

class AddPlayer extends Component {
  render() {
    return (
        <div id="container">  
        <PageHeader className="tracking-in-expand-fwd-top" style={{fontFamily: "'Alfa Slab One', cursive",color:"green",textShadow:"2px 2px lightgreen"}}>AddPlayer</PageHeader>       
        <ButtonGroup className="bounce-in-top" vertical>
       
        <Button onClick={()=>this.passToScreen(0)}  onMouseLeave={()=>this.hideIcon(0)} onMouseEnter={()=>this.showIcon(0)} bsStyle="success"> <i style={{float:"left"}} class="fas fa-user-plus"> &nbsp;Add Player</i>  </Button> 
        <Button onMouseLeave={()=>this.hideIcon(1)} onMouseEnter={()=>this.showIcon(1)} bsStyle="success"> <i style={{float:"left"}} class="fas fa-user-minus"> &nbsp;Remove Player</i> </Button>
        
        <Button onMouseLeave={()=>this.hideIcon(2)} onMouseEnter={()=>this.showIcon(2)}  bsStyle="success"> <i style={{float:"left"}} class="fas fa-user-edit"> &nbsp;Edit Player</i></Button>
       
        <Button onMouseLeave={()=>this.hideIcon(3)} onMouseEnter={()=>this.showIcon(3)} bsStyle="success"> <i style={{float:"left"}} class="fas fa-credit-card"> &nbsp;Payment</i></Button>
       
        <Button onMouseLeave={()=>this.hideIcon(4)} onMouseEnter={()=>this.showIcon(4)}  bsStyle="success"> <i style={{float:"left"}} class="fas fa-futbol"> &nbsp;Start Game</i></Button>
        <Button on onMouseLeave={()=>this.hideIcon(5)} onMouseEnter={()=>this.showIcon(5)}  bsStyle="success"> <i style={{float:"left"}} class="fas fa-users"> &nbsp;Make Teams</i> </Button>

      </ButtonGroup>
    </div>
    )
  }
}

export default withRouter(AddPlayer);
