import React, { Component } from 'react'
import { withRouter } from "react-router-dom";
import {PageHeader,FormGroup,Button,FormControl,Alert} from 'react-bootstrap'
import StarRatingComponent from 'react-star-rating-component';
import './AddPlayer.css'

class AddPlayer extends Component {
 
    state = {
        value: '',
        rating:1,
        validationStatus:"error",
        show:false
        
      };
    
  
    
    handleChange=(e)=> {
      this.setState({ value: e.target.value});

    }

    onStarClick(nextValue, prevValue, name) {
        this.setState({rating: nextValue});
      }
 
    addClicked=()=>{
        this.setState({ show: true });
        // this.props.history.push("/");
    }
 
    render() {
        const { rating } = this.state;
        const validationStatus=this.state.value==="".slice(" ")?"error":"success";
    return (
        <div id="container">  
        <PageHeader className="tracking-in-expand-fwd-top" style={{fontFamily: "'Alfa Slab One', cursive",color:"green",textShadow:"2px 2px lightgreen"}}>AddPlayer</PageHeader>           
        <form id="myForm">
        <FormGroup  controlId="formBasicText" validationState={validationStatus}>
        <FormControl type="text" value={this.state.value} placeholder="Enter Player Name" onChange={this.handleChange}/>
        <FormControl.Feedback />  
        <StarRatingComponent 
          name="rate1" 
          starCount={5}
          value={rating}
          onStarClick={this.onStarClick.bind(this)}
        />
        </FormGroup>
        </form>
        <Alert bsStyle="danger" onDismiss={this.handleDismiss}></Alert>
        <Button onClick={this.addClicked} className={this.state.value==="".slice(" ")?null :"jello-diagonal-2"} bsStyle="primary" type='submit' disabled={this.state.value==="".slice(" ")?true:false}><i class="fas fa-plus">&nbsp; Add</i></Button>
        </div>
    )
  }
}

export default withRouter(AddPlayer);
