import React, { Component } from 'react'
import {PageHeader,FormGroup,FormControl,Button} from 'react-bootstrap'
import { withRouter } from "react-router-dom"
import './EditPlayer.css'

class EditPlayer extends Component {
 
  state={
    validationStatus:"error",
    allow:false,
    value:"",
    visibility:"hidden",
    value2:"",
    visibility2:"hidden",
    value3:0
}


 handleChange=(e)=>{
  let check=null;
  check=this.props.mainstate.players.find(player=>player.value===e.target.value.split(" ").join(""))
  let x=check=== undefined ?"error":"success"
  this.setState({validationStatus:x,allow:x==="success"?true:false,value:e.target.value,visibility:check=== undefined ?"hidden":"visible"})
}

handleChange2=(e)=>{
  this.setState({value2:e.target.value})
}

changePlayer=()=>{
  let objIndex = this.props.mainstate.players.findIndex(player => player.value === this.state.value);

//Update object's name property.
this.props.mainstate.players[objIndex].value=this.state.value2
this.props.mainstate.players[objIndex].rating=this.state.value3
this.props.setSt({ backdropVisibility:"visible",textOfBackdrop:"Edited!"})
this.props.history.push('/');
}


numChange=(e)=>{
this.setState({value3:e.target.value})

}

 
  render() {
    return (
      <div id="container">
         <PageHeader className="tracking-in-expand-fwd-top" style={{fontFamily: "'Alfa Slab One', cursive",color:"green",textShadow:"2px 2px lightgreen"}}>Edit Player</PageHeader> 
         <form id="myForm">
        <FormGroup  controlId="formBasicText" validationState={this.state.validationStatus}>
        <FormControl type="text" maxLength={20} value={this.state.value} placeholder="Enter Player Name" onChange={this.handleChange}/>
        <FormControl.Feedback />  
        </FormGroup>
        </form>
        <form id="myForm" style={{ visibility:this.state.visibility}} className={this.state.visibility==="visible"?"scale-in-ver-center":"slide-out-bck-center"}>
        <FormGroup  controlId="formBasicText">
        <FormControl type="text" maxLength={20} value={this.state.value2} placeholder="Enter New Name" onChange={this.handleChange2}/>
        <input style={{margin:"10px",width:"20%"}} onChange={this.numChange} value={this.state.value3}  type="number" name="quantity" min="1" max="5" />
        <FormControl.Feedback />  
        </FormGroup>
        </form>
        <Button bsStyle="warning" onClick={this.changePlayer} style={{visibility:this.state.value2!==""?"visible":"hidden"}}
        className={this.state.value2!==""?"slide-in-elliptic-top-fwd":null}
        >Change</Button>
      </div>
    )
  }
}

export default withRouter(EditPlayer);
