import React, { Component } from 'react'
import {PageHeader,FormGroup,FormControl,Button} from 'react-bootstrap'
import { withRouter } from "react-router-dom";
 class RemovePlayer extends Component {
  
  state={
      validationStatus:"error",
      allow:false,
      value:""
  }
  
  handleChange=(e)=>{
    let check=null;
    check=this.props.mainstate.players.find(player=>player.value===e.target.value)
    console.log(check);
    let x=check=== -1 ?"error":"success"
    this.setState({validationStatus:x,allow:x==="success"?true:false,value:e.target.value})
  }

  removeClicked=(value)=>{

    let temp=[...this.props.mainstate.players]
    temp.splice(temp.indexOf(value),1)
    this.props.mainstate.players=[...temp]
    this.props.history.push('/')
  }
  
  
  render() {
    return (
      <div id="container">
         <PageHeader className="tracking-in-expand-fwd-top" style={{fontFamily: "'Alfa Slab One', cursive",color:"green",textShadow:"2px 2px lightgreen"}}>Remove Player</PageHeader>
         <form id="myForm">
        <FormGroup  controlId="formBasicText" validationState={this.state.validationStatus}>
        <FormControl type="text" maxLength={20} value={this.state.value} placeholder="Enter Player Name" onChange={this.handleChange}/>
        <FormControl.Feedback />  
        </FormGroup>
        </form>
        <Button onClick={()=>this.removeClicked(this.state.value)} className={this.state.allow===true?"jello-diagonal-2":null} bsStyle="primary" type='submit' disabled={!this.state.allow}><i class="fas fa-minus">&nbsp; Remove</i></Button>
      </div>
    )
  }
}

export default withRouter(RemovePlayer);
