import React, { Component } from 'react'
import {ListGroupItem} from 'react-bootstrap'

export default class PlayerPayment extends Component {
 
   state={
       clicked:false
   }
 
 
 
    render() {
    return (
      <div>
         {this.props.mainstate.players.map((player,index)=><ListGroupItem key={index} bsStyle={this.state.clicked ? "success":"danger"} onClick={this.add} id="paymentItem" >{index+1}.&nbsp;Name:{player.value}</ListGroupItem> )}
      </div>
    )
  }
}
