import React, { Component } from 'react'
import {PageHeader} from 'react-bootstrap'
import './Style.css'
import {Form,FormGroup,FormControl,Col,Checkbox,Button,ControlLabel} from 'react-bootstrap'

export default class Login extends Component {

    state={
    PasswordValue:"",
    NameValue:"",
    CheckBoxValue:false
    }

    EmailhandleChange=(e)=>this.setState({EmailValue:e.target.value})
    PasswordhandleChange=(e)=>this.setState({PasswordValue:e.target.value})
    NamehandleChange=(e)=>this.setState({NameValue:e.target.value})
    
    clickedCheck=()=>{
        let temp=!this.state.CheckBoxValue
        this.setState({CheckBoxValue:temp})
      }
    


    signinHandler=()=>{
      console.log("In Sign in!")
        if(this.state.CheckBoxValue==true)
            localStorage.setItem(`Users`,JSON.stringify({Name:this.state.NameValue,Password:this.state.PasswordValue})) 
       this.props.mainstate.users.map(user=>console.log(`In Login:${user}`))
        if(this.props.mainstate.users.some(user=>user.Username==this.state.NameValue && user.Password==this.state.PasswordValue)){
            this.props.setSt({isAuth:true})
            this.props.history.push("/Menu")  
        }
        }

//  componentDidMount=()=>{
//      const x=localStorage.getItem(`Users`)
//           if (x!=null){//Check if exist user in local storage    
//                this.props.setSt({isAuth:true})
//                this.props.history.push("/Menu")    
//         }
//  }

    render() {
       return (
        <div id="container">
                <PageHeader 
                className="tracking-in-expand-fwd-top" 
                style={{fontFamily: "'Alfa Slab One', cursive",color:"green",textShadow:"2px 2px lightgreen"}}>
                Login
                </PageHeader> 
                <Form id="RegisterForm" horizontal>
            
            <FormGroup controlId="formHorizontalName">
            <Col componentClass={ControlLabel} sm={2}>
            Username
            </Col>
            <Col sm={8}>
            <FormControl type="text" placeholder="Enter Username"  value={this.state.NameValue} onChange={(e)=>this.NamehandleChange(e)} />
            </Col>
            </FormGroup>

            <FormGroup controlId="formHorizontalPassword">
              <Col componentClass={ControlLabel} sm={2}>
                Password
              </Col>
              <Col sm={8}>
                <FormControl type="password" placeholder="Password"  value={this.state.PasswordValue} onChange={(e)=>this.PasswordhandleChange(e)}/>
              </Col>
            </FormGroup>

             <FormGroup>
              <Col smOffset={2} sm={8}>
                <Checkbox   onClick={this.clickedCheck}>Remember me</Checkbox>
              </Col>
            </FormGroup>

            <FormGroup>
                    <Col smOffset={2} sm={8}>
                    <Button type="submit" onClick={this.signinHandler}>Sign in</Button>
                    </Col>
            </FormGroup> 
            </Form>
        </div>
    )
  }
}
