export function handleChange(e) {
    this.setState({ value: e.target.value});

  }

export function  onStarClick(nextValue, prevValue, name) {
      this.setState({rating: nextValue});
    }

export function  addClicked(value,rating){
    let temp=this.props.mainstate.players
    temp.push({value,rating})
    this.props.mainstate.players=[...temp]
    this.props.setSt({ backdropVisibility:"visible",textOfBackdrop:"Successfully added new player!"})
    this.props.history.push('/')
     
  }
