import React, { Component } from 'react'
import { withRouter } from "react-router-dom";
import {PageHeader,FormGroup,ControlLabel,FormControl,HelpBlock} from 'react-bootstrap'
import StarRatingComponent from 'react-star-rating-component';
import './AddPlayer.css'

class AddPlayer extends Component {
 
    state = {
        value: '',
        rating:1
      };
    
  
    
    handleChange=(e)=> {
      this.setState({ value: e.target.value });
    }

    onStarClick(nextValue, prevValue, name) {
        this.setState({rating: nextValue});
      }
 
 
    render() {
        const { rating } = this.state;
    return (
        <div id="container">  
        <PageHeader className="tracking-in-expand-fwd-top" style={{fontFamily: "'Alfa Slab One', cursive",color:"green",textShadow:"2px 2px lightgreen"}}>AddPlayer</PageHeader>           
        <form id="myForm">
        <FormGroup  controlId="formBasicText">
        <FormControl type="text" value={this.state.value} placeholder="Enter Player Name" onChange={this.handleChange}/>
        <FormControl.Feedback />  
        <StarRatingComponent 
          name="rate1" 
          starCount={5}
          value={rating}
          onStarClick={this.onStarClick.bind(this)}
        />
        </FormGroup>
      </form>
        </div>
    )
  }
}

export default withRouter(AddPlayer);
