import React, { Component } from 'react'
import {PageHeader} from 'react-bootstrap'


export default class MakeTeams extends Component {
 
  state={
    players1:[],
    players2:[],
    players3:[],
    players4:[],
    players5:[]
  }

  render() {
    (this.props.mainstate.players.map(player=>{
      switch(player.rating){
        
        case 1:this.state.players1.push(player)
               break;
        case 2:this.state.players2.push(player)
        break;
        case 3:this.state.players3.push(player)
        break;
        case 4:this.state.players4.push(player)
        break;
        case 5:this.state.players5.push(player)
        break;
        default:
        break;
      }
    }))
    return (
      <div id="container">
         <PageHeader className="tracking-in-expand-fwd-top" style={{fontFamily: "'Alfa Slab One', cursive",color:"green",textShadow:"2px 2px lightgreen"}}>Make Teams</PageHeader> 
         <div>{this.state.players1}</div>
         <div>{this.state.players2}</div>
         <div>{this.state.players3}</div>
         <div>{this.state.players4}</div>
         <div>{this.state.players5}</div>
     
     
      </div>
    )
  }
}
