import React, { Component } from 'react'
import {ButtonGroup,Button,PageHeader,Image} from 'react-bootstrap'
import ball from '../../Icons/ball_ico.png'
import '../Menu/Menu.css'

export default class Menu extends Component {
 
 state={
   display:["hidden","hidden","hidden","hidden","hidden","hidden"]

 }
 
 showIcon=(num)=>{
   let arrDisplay = [...this.state.display];
   arrDisplay[num]="visible";
   this.setState({display:arrDisplay})

 }

 hideIcon=(num)=>{
  let arrDisplay=["hidden","hidden","hidden","hidden","hidden","hidden"];
  this.setState({display:arrDisplay})

}
 
  render() {
    return (
      
      <div id="container">
        <PageHeader className="tracking-in-expand-fwd-top" style={{fontFamily: "'Alfa Slab One', cursive",color:"green",textShadow:"2px 2px lightgreen"}}>Menu</PageHeader>       
        <Image src={ball} rounded style={{position:"absolute",left:"-23%",visibility:this.state.display[0]}} />
        <Image src={ball} rounded style={{position:"absolute",top:"45%",left:"-23%",visibility:this.state.display[1]}} />
        <Image src={ball} rounded style={{position:"absolute",top:"55%",left:"-23%",visibility:this.state.display[2]}} />
        <Image src={ball} rounded style={{position:"absolute",top:"67%",left:"-23%",visibility:this.state.display[3]}} />
        <Image src={ball} rounded style={{position:"absolute",top:"77%",left:"-23%",visibility:this.state.display[4]}} />
        <Image src={ball} rounded style={{position:"absolute",top:"90%",left:"-23%",visibility:this.state.display[5]}} />
        <ButtonGroup className="bounce-in-top" vertical>
        <Button  onMouseLeave={()=>this.hideIcon(0)} onMouseEnter={()=>this.showIcon(0)} bsStyle="success"> <i style={{float:"left"}} class="fas fa-user-plus"> &nbsp;Add Player</i>  </Button> 
          <Button onMouseLeave={()=>this.hideIcon(1)} onMouseEnter={()=>this.showIcon(1)} bsStyle="success"> <i style={{float:"left"}} class="fas fa-user-minus"> &nbsp;Remove Player</i> </Button>
          <Button onMouseLeave={()=>this.hideIcon(2)} onMouseEnter={()=>this.showIcon(2)}  bsStyle="success"> <i style={{float:"left"}} class="fas fa-user-edit"> &nbsp;Edit Player</i></Button>
          <Button onMouseLeave={()=>this.hideIcon(3)} onMouseEnter={()=>this.showIcon(3)} bsStyle="success"> <i style={{float:"left"}} class="fas fa-credit-card"> &nbsp;Payment</i></Button>
          <Button onMouseLeave={()=>this.hideIcon(4)} onMouseEnter={()=>this.showIcon(4)}  bsStyle="success"> <i style={{float:"left"}} class="fas fa-futbol"> &nbsp;Start Game</i></Button>
          <Button on onMouseLeave={()=>this.hideIcon(5)} onMouseEnter={()=>this.showIcon(5)}  bsStyle="success"> <i style={{float:"left"}} class="fas fa-users"> &nbsp;Make Teams</i> </Button>
        </ButtonGroup>
      </div>
      
    )
  }
}
