import React, { Component } from 'react'
import {PageHeader,ListGroup,ListGroupItem} from 'react-bootstrap'
import {FormGroup,InputGroup,FormControl} from 'react-bootstrap'
import List from '../List/List'

export default class Payment extends Component {
  
  state={
    counter:0,
    gamePrice:0,
    value:10,
    clicked:false
  }
  
  
 add=()=>{
   let temp=this.state.counter;
   temp=Number(temp)+Number(this.state.gamePrice);
   this.setState({counter:Number(temp)});
 }



 changeValue=(e)=>{
this.setState({gamePrice:e.target.value,value:e.target.value,clicked:!this.state.clicked})
 }
  
  render() {
    return (
      <div id="container">
         <PageHeader className="tracking-in-expand-fwd-top" style={{fontFamily: "'Alfa Slab One', cursive",color:"green",textShadow:"2px 2px lightgreen"}}>Payment</PageHeader> 
        <h1 id="counter" style={{color:"white"}}>Counter:{this.state.counter}</h1>
        <FormGroup>
        <InputGroup>
        <InputGroup.Addon> &#8362; </InputGroup.Addon>
        <FormControl type="number" value={this.state.value} onChange={this.changeValue} min={10} max={50}/>
        <InputGroup.Addon>.00</InputGroup.Addon>
        </InputGroup>
        </FormGroup>
         <ListGroup id="paymentList">
           {this.props.mainstate.players.map((player,index)=><ListGroupItem bsStyle={this.state.clicked ? "success":"danger"} onClick={this.add} id="paymentItem" >{index+1}.&nbsp;Name:{player.value}</ListGroupItem> )}
           </ListGroup>;
      </div>
    )
  }
}
