import React, { Component } from 'react'
import soccerPlayer from '../../Images/soccerPlayer.gif'
import {Button} from 'react-bootstrap'

class Backdrop extends Component {
  RemoveBackdrop=()=>this.props.setSt({ backdropVisibility:"hidden"});
  
  render() {
    return (
        <div id="backdrop"
        style={{
         visibility:this.props.mainstate.backdropVisibility,
         position:"absolute",
         top:"-10%",
         width:"100%",
         height:"110%",
         backgroundColor:"black",
         opacity:"0.8",
         border:"2px solid black",
         zIndex:"2"}}>
 
        <div id="SuccessMessage">
         <span style={{
           position:"relative",
           top:"40%",
           color:"green",
           fontWeight:"bold"}}>
           {this.props.mainstate.textOfBackdrop}
           </span>
         <br/>
         <img src={soccerPlayer} id="soccerPlayer" />
         <Button bsStyle="info" onClick={this.RemoveBackdrop}>OK</Button>
       </div>
       </div>
    )
  }
}

export default Backdrop;
