import React, { Component } from 'react'
import {ButtonGroup,Button,PageHeader,Image} from 'react-bootstrap'
import ball from '../../Icons/ball_ico.png'
import '../Menu/Menu.css'
import { withRouter,Link } from "react-router-dom";


 class Menu extends Component {
 
 state={
   display:["hidden","hidden","hidden","hidden","hidden","hidden","hidden"],
   players:[]
   

 }
 
 showIcon=(num)=>{
   let arrDisplay = [...this.state.display];
   arrDisplay[num]="visible";
   this.setState({display:arrDisplay})

 }

 hideIcon=(num)=>{
  let arrDisplay=["hidden","hidden","hidden","hidden","hidden","hidden","hidden"];
  this.setState({display:arrDisplay})

}
 
passToScreen=(num)=>{
 switch(num){
   case(0):
         this.props.history.push("/AddPlayer");
   case(1):
   case(2):
   case(3):
   case(4):
   case(5):
 }
   
}


  render() {
    return (
      <div id="container">  
          <PageHeader className="tracking-in-expand-fwd-top" style={{fontFamily: "'Alfa Slab One', cursive",color:"green",textShadow:"2px 2px lightgreen"}}>Menu</PageHeader>       
          <ButtonGroup className="bounce-in-top" vertical>
          <Image src={ball} rounded style={{position:"absolute",left:"-20%",visibility:this.state.display[0]}} />
          <Button onClick={()=>this.passToScreen(0)}  onMouseLeave={()=>this.hideIcon(0)} onMouseEnter={()=>this.showIcon(0)} bsStyle="success"> <i style={{float:"left"}} class="fas fa-user-plus"> &nbsp;Add Player</i>  </Button> 
          <Button onMouseLeave={()=>this.hideIcon(1)} onMouseEnter={()=>this.showIcon(1)} bsStyle="success"> <i style={{float:"left"}} class="fas fa-user-minus"> &nbsp;Remove Player</i> </Button>
          <Image src={ball} rounded style={{position:"absolute",top:"15%",left:"-20%",visibility:this.state.display[1]}} />  
          <Button onMouseLeave={()=>this.hideIcon(2)} onMouseEnter={()=>this.showIcon(2)}  bsStyle="success"> <i style={{float:"left"}} class="fas fa-user-edit"> &nbsp;Edit Player</i></Button>
          <Image src={ball} rounded style={{position:"absolute",top:"30%",left:"-20%",visibility:this.state.display[2]}} />  
          <Button onMouseLeave={()=>this.hideIcon(3)} onMouseEnter={()=>this.showIcon(3)} bsStyle="success"> <i style={{float:"left"}} class="fas fa-credit-card"> &nbsp;Payment</i></Button>
          <Image src={ball} rounded style={{position:"absolute",top:"45%",left:"-20%",visibility:this.state.display[3]}} />  
          <Button onMouseLeave={()=>this.hideIcon(4)} onMouseEnter={()=>this.showIcon(4)}  bsStyle="success"> <i style={{float:"left"}} class="fas fa-futbol"> &nbsp;Start Game</i></Button>
          <Image src={ball} rounded style={{position:"absolute",top:"58%", left:"-20%",visibility:this.state.display[4]}} />  
          <Button on onMouseLeave={()=>this.hideIcon(5)} onMouseEnter={()=>this.showIcon(5)}  bsStyle="success"> <i style={{float:"left"}} class="fas fa-users"> &nbsp;Make Teams</i> </Button>
          <Image src={ball} rounded style={{position:"absolute",top:"72%",left:"-20%",visibility:this.state.display[5]}} />
          <Button on onMouseLeave={()=>this.hideIcon(5)} onMouseEnter={()=>this.showIcon(6)}  bsStyle="success"><i class="fas fa-clipboard-list"> &nbsp;List of Players</i> </Button>
          <Image src={ball} rounded style={{position:"absolute",top:"87%",left:"-20%",visibility:this.state.display[6]}} />
        </ButtonGroup>
      </div>
      
    )
  }
}

export default withRouter(Menu);
