import React, { Component } from 'react'
import {PageHeader,FormGroup,FormControl} from 'react-bootstrap'
export default class RemovePlayer extends Component {
  
  state={
      validationStatus:"error"    
  }
  
  handleChange=(e)=>{

    let check;
    check=this.props.mainstate.players.indexOf({name:e.target.value})
    this.setState({validationState:check===-1?"error":"success"})


    
  }
  
  
  render() {
    return (
      <div id="container">
         <PageHeader className="tracking-in-expand-fwd-top" style={{fontFamily: "'Alfa Slab One', cursive",color:"green",textShadow:"2px 2px lightgreen"}}>Remove Player</PageHeader>
         <form id="myForm">
        <FormGroup  controlId="formBasicText" validationState={this.state.validationStatus}>
        <FormControl type="text" maxLength={20} value={this.state.value} placeholder="Enter Player Name" onChange={this.handleChange}/>
        <FormControl.Feedback />  
        </FormGroup>
        </form>
      </div>
    )
  }
}
